$(function(){
    $("form").submit(function(){
        event.preventDefault();

        var url = $(this).data("url");
        var method = $(this).data("method");
        var anmer = $(this).data("notification");

        $.ajax({
            url: url,
            method: method,
            processData: false,
            data: new FormData(this),
            success: function(e){
                if(anmer != undefined)
                    anmer.analise(e);
            }
        });
    });
});